import React, { useState } from 'react';
import axios from 'axios';
import Avatar from '@mui/material/Avatar';
import Button from '@mui/material/Button';
import CssBaseline from '@mui/material/CssBaseline';
import TextField from '@mui/material/TextField';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import Box from '@mui/material/Box';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import VisibilityIcon from '@mui/icons-material/Visibility';
import VisibilityOffIcon from '@mui/icons-material/VisibilityOff';
import Typography from '@mui/material/Typography';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import Grid from '@mui/material/Grid';
import { Link } from '@mui/material';
import Paper from '@mui/material/Paper';
import IconButton from '@mui/material/IconButton';
import { useNavigate } from 'react-router-dom';

const SignIn = () => {
  const [userName, setUserName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [dob, setDob] = useState('');
  const [showPassword, setShowPassword] = useState(false); // State to manage password visibility
  const navigate = useNavigate();

  // Mock authenticated state for demonstration purposes
  const isAuthenticated = false; // Replace this with your authentication logic

  // Handle form submission
  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const data = {
        userName: userName,
        email: email,
        password: password,
        dob: dob
      };
      // Send signup request
      const response = await axios.post("http://localhost:8002/api/sign-up", data);
      console.log("Signup successful:", response.data);
      navigate("/login"); // Redirect to login page after successful signup
    } catch (error) {
      console.log("Error signing up:", error);
      if (error.response && error.response.data.msg === "Username already taken") {
        setError("Username already taken. Please choose a different username.");
      } else {
        setError("An error occurred. Please try again later.");
      }
    }
  };

  // Redirect to login page if user clicks "Already have an account? Log In" link and is already authenticated
  const handleLoginLinkClick = () => {
    if (isAuthenticated) {
      navigate("/login");
    }
  };

  return (
    <ThemeProvider theme={createTheme()}>
      <Grid container component="main" sx={{ height: '100vh' }}>
        <CssBaseline />
        <Grid
          item
          xs={false}
          sm={4}
          md={7}
          sx={{
            backgroundImage: 'url(https://source.unsplash.com/random?wallpapers)',
            backgroundRepeat: 'no-repeat',
            backgroundColor: (t) =>
              t.palette.mode === 'light' ? t.palette.grey[50] : t.palette.grey[900],
            backgroundSize: 'cover',
            backgroundPosition: 'center',
          }}
        />
        <Grid item xs={12} sm={8} md={5} component={Paper} elevation={6} square>
          <Box
            sx={{
              my: 8,
              mx: 4,
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
            }}
          >
            <Avatar sx={{ m: 1, bgcolor: 'secondary.main' }}>
              <LockOutlinedIcon />
            </Avatar>
            <Typography component="h1" variant="h5">
              Sign up
            </Typography>
            <Box component="form" onSubmit={handleSubmit} noValidate sx={{ mt: 3 }}>
              {/* Form fields */}
            </Box>
            {error && <Typography color="error">{error}</Typography>}
            <Grid container>
              <Grid item>
                <Link
                  href="#"
                  variant="body2"
                  onClick={handleLoginLinkClick} // Handle click event for "Already have an account? Log In" link
                >
                  {"Already have an account? Log In"}
                </Link>
              </Grid>
            </Grid>
          </Box>
        </Grid>
      </Grid>
    </ThemeProvider>
  );
}

export default SignIn;
