import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Signup = () => {
    const [userName, setUserName] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const history = useNavigate();

    const handleSignup = async (e) => {
        e.preventDefault();
        try {
            const data = {
                userName: userName,
                password: password
            }
            // Send signup data to the server
            const response = await axios.post("http://localhost:8002/api/sign-up", data);
            console.log("Signup successful:", response.data);
            // Redirect to login page after successful signup
            history("/login");
        } catch (error) {
            console.error("Error signing up:", error);
            // Check if the error response contains the message indicating the username is already taken
            if (error.response.data.msg === "Username already taken") {
                setError("Username already taken. Please choose a different username.");
            }
        }
    }

    return (
        <div className="signup-container">
            <div className="signup-form">
                <form onSubmit={handleSignup}>
                    <div className="mb-3">
                        <label htmlFor="userName" className="form-label">UserName</label>
                        <input
                            type="text"
                            className="form-control"
                            id="userName"
                            value={userName}
                            onChange={(e) => setUserName(e.target.value)}
                            aria-describedby="userNameHelp"
                        />
                        <div id="userNameHelp" className="form-text">We'll never share your userName with anyone else.</div>
                    </div>
                    <div className="mb-3">
                        <label htmlFor="password" className="form-label">Password</label>
                        <input
                            type="password"
                            className="form-control"
                            id="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                        />
                    </div>
                    {error && <div className="alert alert-danger" role="alert">{error}</div>}
                    <button type="submit" className="btn btn-danger">Signup</button>
                </form>
            </div>
        </div>
    );
}

export default Signup;
