import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Product.css'; // Import CSS file

export default function Product() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:8002/api/productList')
      .then(response => {
        setProducts(response.data.data);
      })
      .catch(error => {
        console.error('Error fetching products:', error);
      });
  }, []);

  return (
    <div>
      {/* Navbar */}
      <nav className="navbar navbar-expand-lg navbar-light bg-light">
        <div className="container-fluid">
          <button className="navbar-brand" onClick={(e) => e.preventDefault()}>Navbar</button>
          <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarNav">
            <ul className="navbar-nav">
              <li className="nav-item">
                <button className="nav-link active" aria-current="page" onClick={(e) => e.preventDefault()}>Home</button>
              </li>
              <li className="nav-item">
                <button className="nav-link" onClick={(e) => e.preventDefault()}>Features</button>
              </li>
              <li className="nav-item">
                <button className="nav-link" onClick={(e) => e.preventDefault()}>Pricing</button>
              </li>
              <li className="nav-item">
                <button className="nav-link disabled" tabIndex="-1" aria-disabled="true" onClick={(e) => e.preventDefault()}>Disabled</button>
              </li>
            </ul>
          </div>
        </div>
      </nav>

      {/* Product list */}
      <div className="product-list">
        {products.map(product => (
          <div className="card" style={{ width: '18rem', margin: '1rem' }} key={product._id}>
            <img src={product.url} className="card-img-top" alt={product.title} />
            <div className="card-body">
              <h5 className="card-title">{product.title}</h5>
              <p className="card-text">{product.description}</p>
              <p className="card-text">Price: ${product.price}</p>
              <p className="card-text">Category: {product.category}</p>
              <button className="btn btn-primary">Read More</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
