import React, { useState, useEffect } from 'react';
import Productitem from'./Productitem';

const Items = () => {
    const [items, setItems] = useState([]);
  
    useEffect(() => {
      const fetchData = async () => {
        try {
          let url = "https://fakestoreapi.com/products";
          let data = await fetch(url);
          let parsedData = await data.json();
          setItems(parsedData);
        } catch (error) {
          console.error("Error fetching data:", error);
        }
      };
      fetchData();
    }, []);
  
    // Return the state variable
    return items;
  };
  

  export default function Products() {
    const items = Items(); // Call the Items function to get the items
  
    return (
      <div>
        <div className="container my-3">
          <h1>My Products</h1>
          <div className="row">
            {items.map((element) => (
              <div className="col-md-4" key={element.image}>
                <Productitem
                  title={element.title ? element.title : ""}
                  description={element.description ? element.description.slice(0, 88) : ""}
                  imageUrl={element.image ? element.image : "https://cdn.ndtv.com/common/images/ogndtv.png"}
                  itemImage={element.image}
                />
              </div>
            ))}
            <div className="container">
              <button type="button" className="btn btn-dark"> &larr; Previous</button>
              <button type="button" className="btn btn-dark"> Next &rarr;</button>
            </div>
          </div>
        </div>
      </div>
    );
  }
