import React, { useState } from 'react';
import axios from 'axios';


export default function AddProduct() {
        const[title,setTitle]=useState('');
        const[price,setPrice]=useState('');
        const[description,setDescription]=useState('');
        const[category,setCategory]=useState('');
        const[url,setUrl]=useState('');
        const[message,setMessage]=useState('');

        const handleSubmit=(event)=>{
            event.preventDefault();
           if(!title || !price || !description || !category ){
            setMessage('Please fill all the fields');
            return;
           }



            const newProduct={title,price,description,category,url};
            axios.post("http://localhost:8002/api/addProduct",newProduct)
            .then((resp)=>{console.log("Product added successfully",resp.data)
            setMessage('Product added successfully.');
            // Reset form fields after successful submission
            setTitle('');
            setPrice('');
            setDescription('');
            setCategory('');
            setUrl('');
            setTimeout(() => {
              setMessage('');
          }, 1000);
            })

            
            .catch((error)=>{console.log("Error addinf product",error)}) 

          
        }

        return (
          <div>
             <h2>Add New Product</h2>
      <form>
        <input
          type="text"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          placeholder="Title"
          required
        />
        <input
          type="number"
          value={price}
          onChange={(e) => setPrice(e.target.value)}
          placeholder="Price"
          required
        />
        <input
          type="text"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          placeholder="Description"
          required
        />
        <input
          type="text"
          value={category}
          onChange={(e) => setCategory(e.target.value)}
          placeholder="Category"
          required
        />
        <input
          type="text"
          value={url}
          onChange={(e) => setUrl(e.target.value)}
          placeholder="Image URL"
          required
        />
        <button type="submit" onClick={handleSubmit}>Add Product</button>
        
      </form>
      {message && <p>{message}</p>}
          </div>
        )
    }
