import React, { useState } from 'react';
import axios from 'axios';
import { Button, Typography, Container, TextField, Box } from '@mui/material';

const ProfilePictureUpload = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [uploadError, setUploadError] = useState(null);

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleUpload = async () => {
    if (!selectedFile) {
      setUploadError('Please select a file.');
      return;
    }

    const formData = new FormData();
    formData.append('profileImage', selectedFile);

    try {
      const response = await axios.post('http://localhost:8002/api/upload-profile-picture', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      console.log('Upload successful:', response.data);
      // Handle success, such as displaying a success message or updating user profile
    } catch (error) {
      console.error('Error uploading profile picture:', error);
      // Handle error, such as displaying an error message to the user
      setUploadError('An error occurred. Please try again later.');
    }
  };

  return (
    <Container maxWidth="sm">
      <Box mt={5} textAlign="center">
        <Typography variant="h4" gutterBottom>
          Upload Profile Picture
        </Typography>
        <input type="file" accept="image/*" onChange={handleFileChange} />
        <Button variant="contained" color="primary" onClick={handleUpload} style={{ marginTop: '20px' }}>
          Upload
        </Button>
        {uploadError && <Typography color="error">{uploadError}</Typography>}
      </Box>
    </Container>
  );
};

export default ProfilePictureUpload;
