import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Portfolio() {
  const [portfolio, setPortfolio] = useState([]);

  useEffect(() => {
    axios.post("http://localhost:8002/api/productList")
      .then(resp => {
        setPortfolio(resp.data.data);
      })
      .catch(error => {
        console.log(error);
      });
  }, []);

  return (
    <>
      <section id="portfolio" className="portfolio section">
        {/* Section Title */}
        <div className="container section-title">
          <h2>Portfolio</h2>
          <p>
            Necessitatibus eius consequatur ex aliquid fuga eum quidem sint
            consectetur velit
          </p>
        </div>
        {/* End Section Title */}
        <div className="container">
          <div
            className="isotope-layout"
            data-default-filter="*"
            data-layout="masonry"
            data-sort="original-order"
          >
            <ul className="portfolio-filters isotope-filters">
              <li data-filter="*" className="filter-active">All</li>
              <li data-filter=".filter-app">App</li>
              <li data-filter=".filter-product">Product</li>
              <li data-filter=".filter-branding">Branding</li>
            </ul>
            {/* End Portfolio Filters */}
            <div className="row gy-4 isotope-container">
              {portfolio.map((item) => {
                return (
                  <div key={item.id} className={`col-lg-4 col-md-6 portfolio-item isotope-item filter-${item.category.toLowerCase()}`}>
                    <img src={item.url} className="img-fluid" alt="" />
                    <div className="portfolio-info">
                      <h4>{item.title}</h4>
                      <p>{item.description}</p>
                      <p>${item.price}</p>
                      <p>{item.category}</p>
                      <a
                        href={item.url}
                        title={item.title}
                        data-gallery={`portfolio-gallery-${item.category.toLowerCase()}`}
                        className="glightbox preview-link"
                      >
                        <i className="bi bi-zoom-in" />
                      </a>
                      <a
                        href="portfolio-details.html"
                        title="More Details"
                        className="details-link"
                      >
                        <i className="bi bi-link-45deg" />
                      </a>
                    </div>
                  </div>
                );
              })}
              {/* End Portfolio Item */}
            </div>
            {/* End Portfolio Container */}
          </div>
        </div>
      </section>
    </>
  );
}

export default Portfolio;
