import React, { useState, useEffect } from 'react';
import axios from 'axios';
import portfolioImage1 from '../assets/img/masonry-portfolio/masonry-portfolio-1.jpg';
import portfolioImage2 from '../assets/img/masonry-portfolio/masonry-portfolio-2.jpg';
import portfolioImage3 from '../assets/img/masonry-portfolio/masonry-portfolio-3.jpg';
import portfolioImage4 from '../assets/img/masonry-portfolio/masonry-portfolio-4.jpg';
import portfolioImage5 from '../assets/img/masonry-portfolio/masonry-portfolio-5.jpg';
import portfolioImage6 from '../assets/img/masonry-portfolio/masonry-portfolio-6.jpg';
import portfolioImage7 from '../assets/img/masonry-portfolio/masonry-portfolio-7.jpg';
import portfolioImage8 from '../assets/img/masonry-portfolio/masonry-portfolio-8.jpg';
import portfolioImage9 from '../assets/img/masonry-portfolio/masonry-portfolio-9.jpg';

function Portfolio() {
  const [portfolioItems, setPortfolioItems] = useState([]);
  const [filter, setFilter] = useState('*');

  useEffect(() => {
    // Fetch data from backend
    axios.get('http://your-backend-api-url/portfolio-items')
      .then(response => {
        setPortfolioItems(response.data);
      })
      .catch(error => {
        console.error('Error fetching portfolio items:', error);
      });
  }, []);

  const handleFilterClick = (filter) => {
    setFilter(filter);
  };

  const filteredItems = portfolioItems.filter(item => {
    return filter === '*' || item.category === filter;
  });

  return (
    <>
      <section id="portfolio" className="portfolio section">
        <div className="container section-title">
          <h2>Portfolio</h2>
          <p>Necessitatibus eius consequatur ex aliquid fuga eum quidem sint consectetur velit</p>
        </div>
        <div className="container">
          <div className="isotope-layout" data-default-filter="*" data-layout="masonry" data-sort="original-order">
            <ul className="portfolio-filters isotope-filters">
              <li data-filter="*" className={filter === '*' ? 'filter-active' : ''} onClick={() => handleFilterClick('*')}>All</li>
              <li data-filter="App" className={filter === 'App' ? 'filter-active' : ''} onClick={() => handleFilterClick('App')}>App</li>
              <li data-filter="Product" className={filter === 'Product' ? 'filter-active' : ''} onClick={() => handleFilterClick('Product')}>Product</li>
              <li data-filter="Branding" className={filter === 'Branding' ? 'filter-active' : ''} onClick={() => handleFilterClick('Branding')}>Branding</li>
            </ul>
            <div className="row gy-4 isotope-container">
              {filteredItems.map(item => (
                <div key={item.id} className={`col-lg-4 col-md-6 portfolio-item isotope-item filter-${item.category}`}>
                  <img src={item.image} className="img-fluid" alt={item.title} />
                  <div className="portfolio-info">
                    <h4>{item.title}</h4>
                    <p>{item.description}</p>
                    <a href={item.image} title={item.title} data-gallery={`portfolio-gallery-${item.category}`} className="glightbox preview-link">
                      <i className="bi bi-zoom-in" />
                    </a>
                    <a href={`portfolio-details.html?id=${item.id}`} title="More Details" className="details-link">
                      <i className="bi bi-link-45deg" />
                    </a>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </section>
    </>
  );
}

export default Portfolio;
