import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Portfolio() {
  const [portfolio, setPortfolio] = useState([]);
  const [cart, setCart] = useState([]);

  useEffect(() => {
    axios.post("http://localhost:8002/api/productList")
      .then(resp => {
        setPortfolio(resp.data.data.map(item => ({ ...item, quantity: 0 })));
      })
      .catch(error => {
        console.log(error);
      });
  }, []);

  const addToCart = (item) => {
    const updatedCart = [...cart];
    const index = updatedCart.findIndex(cartItem => cartItem.id === item.id);
    if (index !== -1) {
      updatedCart[index].quantity++;
    } else {
      updatedCart.push({ ...item, quantity: 1 });
    }
    setCart(updatedCart);
  };

  const removeFromCart = (item) => {
    const updatedCart = [...cart];
    const index = updatedCart.findIndex(cartItem => cartItem.id === item.id);
    if (index !== -1) {
      updatedCart.splice(index, 1);
    }
    setCart(updatedCart);
  };

  const incrementQuantity = (item) => {
    const updatedCart = [...cart];
    const index = updatedCart.findIndex(cartItem => cartItem.id === item.id);
    if (index !== -1) {
      updatedCart[index].quantity++;
    }
    setCart(updatedCart);
  };

  const decrementQuantity = (item) => {
    const updatedCart = [...cart];
    const index = updatedCart.findIndex(cartItem => cartItem.id === item.id);
    if (index !== -1 && updatedCart[index].quantity > 0) {
      updatedCart[index].quantity--;
      if (updatedCart[index].quantity === 0) {
        updatedCart.splice(index, 1);
      }
    }
    setCart(updatedCart);
  };

  return (
    <>
      <div className="container section-title">
        <h2>Portfolio</h2>
        <p>Necessitatibus eius consequatur ex aliquid fuga eum quidem sint consectetur velit</p>
      </div>
      <div className="container">
        <div className="row gy-4">
          {portfolio.map((item, index) => (
            <div key={index} className="col-lg-4 col-md-6">
              <div className="portfolio-item">
                <img src={item.url} className="img-fluid" alt={item.title} />
                <div className="portfolio-info">
                  <h4>{item.title}</h4>
                  <p>{item.description}</p>
                  <p>${item.price}</p>
                  <div className="quantity-controls">
                    <button onClick={() => addToCart(item)} className="btn btn-primary">Add to Cart</button>
                    <div className="quantity">
                      <button onClick={() => decrementQuantity(item)}>-</button>
                      <span>{item.quantity}</span>
                      <button onClick={() => incrementQuantity(item)}>+</button>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </>
  );
}

export default Portfolio;
