import React from 'react';
import heroimage from '../assets/img/hero-bg.jpg';

function Hero() {
  // Add state to track if the user has signed up
  const [signedUp, setSignedUp] = React.useState(false);

  // Function to handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    // Perform sign-up logic here
    // For demonstration purposes, just updating the state to simulate signing up
    setSignedUp(true);
  };

  // If the user has not signed up, display the sign-up form
  if (!signedUp) {
    return (
      <section id="hero" className="hero section">
        <img src={heroimage} alt="Hero background" />
        <div className="container">
          <div className="row">
            <div className="col-lg-5">
              <form
                onSubmit={handleSubmit}
                className="sign-up-form d-flex"
                data-aos="fade-up"
                data-aos-delay={300}
              >
                <input
                  type="text"
                  className="form-control"
                  placeholder="Enter email address"
                />
                <input
                  type="submit"
                  className="btn btn-primary"
                  value="Sign up"
                />
              </form>
            </div>
          </div>
        </div>
      </section>
    );
  }

  // If the user has signed up, display the welcome message
  return (
    <section id="hero" className="hero section">
      <img src={heroimage} alt="Hero background" />
      <div className="container">
        <div className="row">
          <div className="col-lg-10">
            <h2>
              Welcome to Our Website
            </h2>
            <p>
              We are a team of talented designers making websites with Bootstrap
            </p>
          </div>
        </div>
      </div>
    </section>
  );
}

export default Hero;
