import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Portfolio() {
  const [portfolio, setPortfolio] = useState([]);
  const [cart, setCart] = useState([]);

  useEffect(() => {
    fetchPortfolio();
    fetchUserCart(); // Fetch user's cart upon component mount
  }, []);

  const fetchPortfolio = async () => {
    try {
      const response = await axios.get("http://localhost:8002/api/productList");
      const activeProducts = response.data.data.filter(product => !product.is_deleted && product.is_active);
      setPortfolio(activeProducts);
    } catch (error) {
      console.error("Error fetching portfolio:", error);
    }
  };

  const fetchUserCart = async () => {
    try {
      const response = await axios.get("http://localhost:8002/api/cartList", {
        withCredentials: true // Send cookies for authentication
      });
      setCart(response.data.cart);
    } catch (error) {
      console.error("Error fetching user's cart:", error);
    }
  };

  const addToCart = async (productId) => {
    try {
      await axios.post("http://localhost:8002/api/addCart", { productId }, {
        withCredentials: true // Send cookies for authentication
      });
      fetchUserCart(); // Fetch updated cart after adding item
    } catch (error) {
      console.error("Error adding to cart:", error);
    }
  };

  return (
    <div>
      <h2>Portfolio</h2>
      <div className="portfolio-items">
        {portfolio.map(product => (
          <div key={product.id}>
            <h3>{product.title}</h3>
            <p>{product.description}</p>
            <p>${product.price}</p>
            <button onClick={() => addToCart(product.id)}>Add to Cart</button>
          </div>
        ))}
      </div>
      <h2>Cart</h2>
      <div className="cart-items">
        {cart.map(item => (
          <div key={item.id}>
            <h3>{item.title}</h3>
            <p>{item.description}</p>
            <p>${item.price}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Portfolio;
