import React, { useEffect, useState } from 'react';
import './Cart.css';
import { loadStripe } from "@stripe/stripe-js";

function AddToCart() {
  const [cart, setCart] = useState([]);

  // Fetch initial cart items from localStorage or sessionStorage
  useEffect(() => {
    const savedCart = sessionStorage.getItem('cart') || localStorage.getItem('hardikCart');
    if (savedCart) {
      setCart(JSON.parse(savedCart));
    }
  }, []);

  const calculateTotal = () => {
    if (cart.length === 0) {
      return { subtotal: 0, tax: 0, shipping: 0, total: 0 };
    }

    const subtotal = cart.reduce((acc, item) => acc + (item.price * (item.quantity || 1)), 0);
    const tax = subtotal * 0.05;
    const shipping = 5.00;
    const total = subtotal + tax + shipping;
    return { subtotal, tax, shipping, total };
  };

  const updateCartItem = (index, action) => {
    const updatedCart = [...cart]; // Create a copy of the cart array

    if (action === 'add') {
      updatedCart[index].quantity = (updatedCart[index].quantity || 1) + 1;
    } else if (action === 'remove') {
      updatedCart[index].quantity = (updatedCart[index].quantity || 1) - 1;
      if (updatedCart[index].quantity <= 0) {
        updatedCart.splice(index, 1); // Remove the item from the cart
      }
    }

    setCart(updatedCart);
    sessionStorage.setItem('cart', JSON.stringify(updatedCart));
    localStorage.setItem('hardikCart', JSON.stringify(updatedCart));
  };

  const { subtotal, tax, shipping, total } = calculateTotal();

  const makePayment=async()=>{
    try{

    
  const stripe= await loadStripe("pk_test_51PN9vERpIGMlBtUA2NzgETORMVnJkDwQzhLlIzfE9AtJvdIvU5GPdn40t70CU5IkgQPofhiPWPTQa1rfuW1eHBQe00MW3ek16A")
   const body={
    products:cart
   }
   const headers={
    "Content-Type":"application/json"
   }
   const response=await fetch("http://localhost:8002/api/payment",{
    method:"POST",
    headers:headers,
    body:JSON.stringify(body)
   })
   if (!response.ok) {
    throw new Error('Failed to make payment');
  }

   const session=await response.json();
   const result=stripe.redirectToCheckout({
    sessionId:session.id
   })
  }
catch(error){
  console.error('Error making payment:', error);
}
  }

  return (
    <>
      <header id="site-header">
        <div className="container">
          <h1>Shopping cart</h1>
        </div>
      </header>
      <div className="container">
        <section id="cart">
          {cart.length === 0 ? (
            <div><h1>Your cart is empty</h1></div>
          ) : (
            cart.map((item, index) => (
              <article className="product" key={index}>
                <header>
                  <img src={item.url} alt={item.title} />
                  <div className="remove">
                    <button onClick={() => updateCartItem(index, 'remove')}>Remove product</button>
                  </div>
                </header>
                <div className="content">
                  <h1>{item.title}</h1>
                  <p>{item.description}</p>
                  <div className="color" style={{ top: 0 }} />
                  <div className="type" style={{ top: 43 }}>{item.category}</div>
                </div>
                <footer className="content">
                  <span className="qt-minus" onClick={() => updateCartItem(index, 'remove')}>-</span>
                  <span className="qt">{item.quantity || 1}</span>
                  <span className="qt-plus" onClick={() => updateCartItem(index, 'add')}>+</span>
                  <h2 className="full-price">${item.price}</h2>
                  <h2 className="price">${(item.price * (item.quantity || 1)).toFixed(2)}</h2>
                </footer>
              </article>
            ))
          )}
        </section>
      </div>
      <footer id="site-footer">
        <div className="container clearfix">
          <div className="left">
            <h2 className="subtotal">Subtotal: <span>{subtotal.toFixed(2)}</span>$</h2>
            <h3 className="tax">Taxes (5%): <span>{tax.toFixed(2)}</span>$</h3>
            <h3 className="shipping">Shipping: <span>{shipping.toFixed(2)}</span>$</h3>
          </div>
          <div className="right">
            <h1 className="total">Total: <span>{total.toFixed(2)}</span>$</h1>
            <button className="btn" onClick={makePayment}>Checkout</button>
          </div>
        </div>
      </footer>
    </>
  );
}

export default AddToCart;

