import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Cart.css';

function AddToCart() {
  const [cart, setCart] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchCartItems = async () => {
      try {
        const response = await axios.get("http://your-api-url/cartList"); // Adjust the URL to your cartList endpoint
        setCart(response.data.cart);
      } catch (error) {
        setError(error);
      }
    };

    fetchCartItems();
  }, []);

  const addToCart = async (productId) => {
    try {
      const response = await axios.post("http://your-api-url/addToCart", { userId: "userId", productId }); // Adjust the URL to your addToCart endpoint and provide the userId
      console.log(response.data); // Log the response
      // After adding to cart successfully, fetch updated cart items
      const updatedResponse = await axios.get("http://your-api-url/cartList"); // Fetch updated cart items
      setCart(updatedResponse.data.cart); // Update cart state with updated items
    } catch (error) {
      setError(error);
    }
  };

  const calculateTotal = () => {
    if (cart.length === 0) {
      return { subtotal: 0, tax: 0, shipping: 0, total: 0 };
    }

    const subtotal = cart.reduce((acc, item) => acc + (item.price * (item.quantity || 1)), 0);
    const tax = subtotal * 0.05;
    const shipping = 5.00;
    const total = subtotal + tax + shipping;
    return { subtotal, tax, shipping, total };
  };

  const updateCart = (index, action) => {
    const updatedCart = [...cart]; // Create a copy of the cart array
    
    if (action === 'add') {
      // Check if the item already exists in the cart
      const existingItemIndex = updatedCart.findIndex(item => item.id === cart[index].id);

      if (existingItemIndex !== -1) {
        // If item already exists, increase its quantity
        updatedCart[existingItemIndex].quantity = (updatedCart[existingItemIndex].quantity || 1) + 1;
      } else {
        // If item doesn't exist, add it to the cart with quantity 1
        updatedCart[index].quantity = (updatedCart[index].quantity || 1) + 1;
      }
    } else if (action === 'remove') {
      updatedCart[index].quantity = (updatedCart[index].quantity || 1) - 1;
      if (updatedCart[index].quantity <= 0) {
        updatedCart.splice(index, 1);
      }
    }
    
    setCart(updatedCart);
    sessionStorage.setItem('cart', JSON.stringify(updatedCart));
  };

  const { subtotal, tax, shipping, total } = calculateTotal();

  return (
    <>
      <header id="site-header">
        <div className="container">
          <h1>Shopping cart</h1>
        </div>
      </header>
      <div className="container">
        <section id="cart">
          {error ? (
            <div><h1>Error loading cart items</h1></div>
          ) : cart.length === 0 ? (
            <div><h1>Your cart is empty</h1></div>
          ) : (
            cart.map((item, index) => (
              <article className="product" key={index}>
                {/* Display cart item details */}
              </article>
            ))
          )}
        </section>
      </div>
      <footer id="site-footer">
        <div className="container clearfix">
          <div className="left">
            <h2 className="subtotal">Subtotal: <span>{calculateTotal().subtotal.toFixed(2)}</span>$</h2>
            <h3 className="tax">Taxes (5%): <span>{calculateTotal().tax.toFixed(2)}</span>$</h3>
            <h3 className="shipping">Shipping: <span>{calculateTotal().shipping.toFixed(2)}</span>$</h3>
          </div>
          <div className="right">
            <h1 className="total">Total: <span>{calculateTotal().total.toFixed(2)}</span>$</h1>
            <button className="btn">Checkout</button>
          </div>
        </div>
      </footer>
    </>
  );
}

export default AddToCart;
