import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Cart.css';

function AddToCart() {
  const [cart, setCart] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const userId = "6659583e1246e74adaf39b2e"; // Replace this with actual user ID from context or session

  useEffect(() => {
    const fetchCart = async () => {
      try {
        const response = await axios.post('http://localhost:3000/api/cart/list', {
          userId: userId,
          productId: "" // ProductId can be optional in this context
        });
        setCart(response.data.cart.products || []);
        setLoading(false);
      } catch (error) {
        console.error("There was an error fetching the cart!", error);
        setError('Error fetching cart');
        setLoading(false);
      }
    };

    fetchCart();
  }, [userId]);

  const calculateTotal = () => {
    if (cart.length === 0) {
      return { subtotal: 0, tax: 0, shipping: 0, total: 0 };
    }

    const subtotal = cart.reduce((acc, item) => acc + (item.product.price * (item.quantity || 1)), 0);
    const tax = subtotal * 0.05;
    const shipping = 5.00;
    const total = subtotal + tax + shipping;
    return { subtotal, tax, shipping, total };
  };

  const updateCart = (index, action) => {
    const updatedCart = [...cart]; // Create a copy of the cart array
    
    if (action === 'add') {
      updatedCart[index].quantity = (updatedCart[index].quantity || 1) + 1;
    } else if (action === 'remove') {
      updatedCart[index].quantity = (updatedCart[index].quantity || 1) - 1;
      if (updatedCart[index].quantity <= 0) {
        updatedCart.splice(index, 1);
      }
    }

    setCart(updatedCart);
    // Optionally update the cart in the backend
    sessionStorage.setItem('cart', JSON.stringify(updatedCart));
  };

  const { subtotal, tax, shipping, total } = calculateTotal();

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <>
      <header id="site-header">
        <div className="container">
          <h1>Shopping cart</h1>
        </div>
      </header>
      <div className="container">
        <section id="cart">
          {cart.length === 0 ? (
            <div><h1>Your cart is empty</h1></div>
          ) : (
            cart.map((item, index) => (
              <article className="product" key={index}>
                <header>
                  <img src={item.product.url} alt={item.product.title} />
                  <div className="remove">
                    <button onClick={() => updateCart(index, 'remove')}>Remove product</button>
                  </div>
                </header>
                <div className="content">
                  <h1>{item.product.title}</h1>
                  <p>{item.product.description}</p>
                  <div className="color" style={{ top: 0 }} />
                  <div className="type" style={{ top: 43 }}>{item.product.category}</div>
                </div>
                <footer className="content">
                  <span className="qt-minus" onClick={() => updateCart(index, 'remove')}>-</span>
                  <span className="qt">{item.quantity || 1}</span>
                  <span className="qt-plus" onClick={() => updateCart(index, 'add')}>+</span>
                  <h2 className="full-price">${item.product.price}</h2>
                  <h2 className="price">${(item.product.price * (item.quantity || 1)).toFixed(2)}</h2>
                </footer>
              </article>
            ))
          )}
        </section>
      </div>
      <footer id="site-footer">
        <div className="container clearfix">
          <div className="left">
            <h2 className="subtotal">Subtotal: <span>{subtotal.toFixed(2)}</span>$</h2>
            <h3 className="tax">Taxes (5%): <span>{tax.toFixed(2)}</span>$</h3>
            <h3 className="shipping">Shipping: <span>{shipping.toFixed(2)}</span>$</h3>
          </div>
          <div className="right">
            <h1 className="total">Total: <span>{total.toFixed(2)}</span>$</h1>
            <button className="btn">Checkout</button>
          </div>
        </div>
      </footer>
    </>
  );
}

export default AddToCart;
