import React, { useEffect, useState } from 'react';
import './Cart.css';

function AddToCart({ cartItems = [], setCart }) {
  const [cart, setCartState] = useState(cartItems);

  useEffect(() => {
    const storedCart = sessionStorage.getItem('cart');
    if (storedCart) {
      setCartState(JSON.parse(storedCart));
    }
  }, []);

  useEffect(() => {
    setCart(cart);
    sessionStorage.setItem('cart', JSON.stringify(cart));
  }, [cart]);

  const calculateTotal = () => {
    const subtotal = cart.reduce((acc, item) => acc + (item.price * item.quantity), 0);
    const tax = subtotal * 0.05;
    const shipping = 5.00;
    const total = subtotal + tax + shipping;
    return { subtotal, tax, shipping, total };
  };

  const { subtotal, tax, shipping, total } = calculateTotal();

  const increaseQuantity = (id) => {
    setCartState(prevCart =>
      prevCart.map(item =>
        item.id === id ? { ...item, quantity: item.quantity + 1 } : item
      )
    );
  };

  const decreaseQuantity = (id) => {
    setCartState(prevCart =>
      prevCart.map(item =>
        item.id === id && item.quantity > 1
          ? { ...item, quantity: item.quantity - 1 }
          : item
      ).filter(item => item.quantity > 0)
    );
  };

  return (
    <>
      <header id="site-header">
        <div className="container">
          <h1>Shopping cart</h1>
        </div>
      </header>
      <div className="container">
        <section id="cart">
          {cart.length === 0 ? (
            <p>Your cart is empty</p>
          ) : (
            cart.map((item, index) => (
              <article className="product" key={index}>
                <header>
                  <img src={item.url} alt={item.title} />
                  <div className="remove">
                    <h3>Remove product</h3>
                  </div>
                </header>
                <div className="content">
                  <h1>{item.title}</h1>
                  <p>{item.description}</p>
                  <div className="color" style={{ top: 0 }} />
                  <div className="type" style={{ top: 43 }}>{item.category}</div>
                </div>
                <footer className="content">
                  <span className="qt-minus" onClick={() => decreaseQuantity(item.id)}>-</span>
                  <span className="qt">{item.quantity}</span>
                  <span className="qt-plus" onClick={() => increaseQuantity(item.id)}>+</span>
                  <h2 className="full-price">${(item.price * item.quantity).toFixed(2)}</h2>
                  <h2 className="price">${item.price.toFixed(2)}</h2>
                </footer>
              </article>
            ))
          )}
        </section>
      </div>
      <footer id="site-footer">
        <div className="container clearfix">
          <div className="left">
            <h2 className="subtotal">Subtotal: <span>{subtotal.toFixed(2)}</span>€</h2>
            <h3 className="tax">Taxes (5%): <span>{tax.toFixed(2)}</span>€</h3>
            <h3 className="shipping">Shipping: <span>{shipping.toFixed(2)}</span>€</h3>
          </div>
          <div className="right">
            <h1 className="total">Total: <span>{total.toFixed(2)}</span>€</h1>
            <button className="btn">Checkout</button>
          </div>
        </div>
      </footer>
    </>
  );
}

export default AddToCart;
