import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const Success = () => {
    const navigate = useNavigate();

    useEffect(() => {
        // Redirect to dashboard after 3 seconds
        const timer = setTimeout(() => {
            navigate("/dashboard");
        }, 3000);

        return () => {
            clearTimeout(timer);
        };
    }, [navigate]);

    useEffect(() => {
        const userId = sessionStorage.getItem('userId');

        if (!userId) {
            console.error('User ID not found in session storage');
            return;
        }

        // Call the backend function to clear the cart
        const clearCartBackend = async () => {
            try {
                const response = await fetch(`http://localhost:8002/api/clearCart${userId}`, {
                    method: 'DELETE',
                    headers: {
                        'Content-Type': 'application/json'
                    }
                });
                if (response.ok) {
                    console.log('Cart cleared successfully');
                } else {
                    console.error('Failed to clear cart');
                }
            } catch (error) {
                console.error('Error clearing cart:', error);
            }
        };

        clearCartBackend();
    }, []);

    return (
        <div>
            <h1>Payment Successful</h1>
            <p>Thank you for your purchase! You will be redirected to the dashboard shortly.</p>
        </div>
    );
};

export default Success;
