import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Cart() {
  const [cart, setCart] = useState([]);
  const userId = localStorage.getItem('userId'); // Or however you get the logged-in user's ID

  useEffect(() => {
    if (!userId) {
      // Redirect to login/signup if userId is not available
      // For example:
      window.location.href = '/signup';
      return;
    }

    axios.post('http://localhost:8002/api/cartList', { userId })
      .then(resp => {
        if (resp.data.success) {
          setCart(resp.data.cart);
        } else {
          console.log(resp.data.msg);
        }
      })
      .catch(error => {
        console.log(error);
      });
  }, [userId]);

  return (
    <div>
      <h2>Your Cart</h2>
      {cart.length === 0 ? (
        <p>Your cart is empty</p>
      ) : (
        <ul>
          {cart.map(item => (
            <li key={item.product._id}>
              <img src={item.product.url} alt={item.product.title} />
              <h4>{item.product.title}</h4>
              <p>{item.product.description}</p>
              <p>${item.product.price}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default Cart;
