import React, { useState } from 'react';
import { Redirect } from 'react-router-dom';
import heroimage from '../assets/img/hero-bg.jpg';
import SignUp from '../Components/Signup'; 

function Hero() {
  // Add state to track if the user has signed up
  const [signedUp, setSignedUp] = useState(false);

  // Function to handle successful sign-up
  const handleSignUpSuccess = () => {
    setSignedUp(true);
  };

  // If the user has not signed up, display the sign-up form
  if (!signedUp) {
    return <SignUp onSignUpSuccess={handleSignUpSuccess} />;
  }

  // If the user has signed up, display the welcome message
  return (
    <section id="hero" className="hero section">
      <img src={heroimage} alt="Hero background" />
      <div className="container">
        <div className="row">
          <div className="col-lg-10">
            <h2>Welcome to Our Website</h2>
            <p>We are a team of talented designers making websites with Bootstrap</p>
          </div>
        </div>
      </div>
    </section>
  );
}

export default Hero;
