import React, { useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';

const Success = () => {
    const navigate = useNavigate();
    const location = useLocation();

    useEffect(() => {
        const queryParams = new URLSearchParams(location.search);
        const paymentSuccess = queryParams.get('success');

        const clearCart = async () => {
            try {
                const userId = sessionStorage.getItem('userId'); // Get the userId from sessionStorage
                const response = await fetch(`/api/clearCart/${userId}`, {
                    method: 'DELETE',
                    headers: {
                        'Content-Type': 'application/json'
                    }
                });
                if (response.ok) {
                    console.log('Cart cleared successfully');
                } else {
                    console.error('Failed to clear cart');
                }
            } catch (error) {
                console.error('Error clearing cart:', error);
            }
        };

        if (paymentSuccess === 'true') {
            sessionStorage.setItem('paymentSuccess', 'true');

            // Clear the cart if payment was successful
            clearCart();
        } else {
            sessionStorage.setItem('paymentSuccess', 'false');
        }

        const timer = setTimeout(() => {
            navigate("/dashboard");
        }, 3000);

        return () => {
            clearTimeout(timer);
        };
    }, [location, navigate]);

    return (
        <div>
            <h1>Payment Successful</h1>
            <p>Thank you for your purchase!</p>
        </div>
    );
};

export default Success;
