import React, { useEffect, useState ,Link} from 'react';
import axios from 'axios';
import './Cart.css';

function AddToCart({ cartItems = [] }) {
  const [cart, setCart] = useState(cartItems);
  const [error,setError]=useState('');

  useEffect(()=>{
 const fetchCartItems=async ()=>{
  try {
    const response= await axios.get("http://localhost:8002/api/cartList");
    setCart(response.data.data);
  } catch (error) {
    setError(error);
  }
 };
 fetchCartItems();
  },[]);

  const addToCart=async(productId)=>{
    try {
      const response=await axios.post("http://localhost:8002/api/addCart",{userId:"userId",productId});
      console.log(response.data.data);
      const updatedResponse=await axios.get("http://localhost:8002/api/cartList")
      setCart(updatedResponse.data.cart);
    } catch (error) {
      setError(error);

    }
  }

  useEffect(() => {
    const storedCart = sessionStorage.getItem('cart');
    if (storedCart) {
      setCart(JSON.parse(storedCart));
    }
  }, []);

  const calculateTotal = () => {
    if (cart.length === 0) {
      return { subtotal: 0, tax: 0, shipping: 0, total: 0 };
    }

    const subtotal = cart.reduce((acc, item) => acc + (item.price * (item.quantity || 1)), 0);
    const tax = subtotal * 0.05;
    const shipping = 5.00;
    const total = subtotal + tax + shipping;
    return { subtotal, tax, shipping, total };
  };

  const updateCart = (index, action) => {
    const updatedCart = [...cart]; // Create a copy of the cart array
    
    if (action === 'add') {
      // Check if the item already exists in the cart
      const existingItemIndex = updatedCart.findIndex(item => item.id === cart[index].id);

      if (existingItemIndex !== -1) {
        // If item already exists, increase its quantity
        updatedCart[existingItemIndex].quantity = (updatedCart[existingItemIndex].quantity || 1) + 1;
      } else {
        // If item doesn't exist, add it to the cart with quantity 1
        updatedCart[index].quantity = (updatedCart[index].quantity || 1) + 1;
      }
    } else if (action === 'remove') {
      updatedCart[index].quantity = (updatedCart[index].quantity || 1) - 1;
      if (updatedCart[index].quantity <= 0) {
        updatedCart.splice(index, 1);
      }
    }
    
    setCart(updatedCart);
    sessionStorage.setItem('cart', JSON.stringify(updatedCart));
  };

  const { subtotal, tax, shipping, total } = calculateTotal();

  return (
    <>
      <header id="site-header">
        <div className="container">
          <h1>Shopping cart</h1>
        </div>
      </header>
      <div className="container">
        <section id="cart">
          {cart.length === 0 ? (
            <div><h1>Your cart is empty</h1></div>
          ) : (
            cart.map((item, index) => (
              <article className="product" key={index}>
                <header>
                  <img src={item.url} alt={item.title} />
                  <div className="remove">
                    <button onClick={() => updateCart(index, 'remove')}>Remove product</button>
                  </div>
                </header>
                <div className="content">
                  <h1>{item.title}</h1>
                  <p>{item.description}</p>
                  <div className="color" style={{ top: 0 }} />
                  <div className="type" style={{ top: 43 }}>{item.category}</div>
                </div>
                <footer className="content">
                  <span className="qt-minus" onClick={() => updateCart(index, 'remove')}>-</span>
                  <span className="qt">{item.quantity || 1}</span>
                  <span className="qt-plus" onClick={() => updateCart(index, 'add')}>+</span>
                  <h2 className="full-price">${item.price}</h2>
                  <h2 className="price">${(item.price * (item.quantity || 1)).toFixed(2)}</h2>
                </footer>
              </article>
            ))
          )}
        </section>
      </div>
      <footer id="site-footer">
        <div className="container clearfix">
          <div className="left">
            <h2 className="subtotal">Subtotal: <span>{subtotal.toFixed(2)}</span>$</h2>
            <h3 className="tax">Taxes (5%): <span>{tax.toFixed(2)}</span>$</h3>
            <h3 className="shipping">Shipping: <span>{shipping.toFixed(2)}</span>$</h3>
          </div>
          <div className="right">
            <h1 className="total">Total: <span>{total.toFixed(2)}</span>$</h1>
            <button className="btn">Checkout</button>
          </div>
        </div>
      </footer>
    </>
  );
}

export default AddToCart;
