import React, { useEffect, useState } from 'react';
import axios from'axios'
import './Cart.css';
import { loadStripe } from "@stripe/stripe-js";
const stripePromise = loadStripe("pk_test_51PN9vERpIGMlBtUA2NzgETORMVnJkDwQzhLlIzfE9AtJvdIvU5GPdn40t70CU5IkgQPofhiPWPTQa1rfuW1eHBQe00MW3ek16A");

function AddToCart() {
  const [cart, setCart] = useState([]);
  const [error, setError] = useState('');
const userId=sessionStorage.getItem("userId")
  

  useEffect(()=>{
    const data ={
      userId:userId
    }
    axios.post("http://localhost:8002/api/cartList",data).then((res)=>{
      console.log(res)
      if(res.data.success){
        setCart(res.data.result)
      }
    }).catch((err)=>{
      console.log(err)
    })
  },[])
console.log(cart,'fghjk')
