import React, { useEffect, useState } from 'react';
import './Cart.css';

function AddToCart({ cartItems = [] }) {
  const [cart, setCart] = useState(cartItems);

  useEffect(() => {
    const storedCart = localStorage.getItem('cart');
    if (storedCart) {
      setCart(JSON.parse(storedCart));
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('cart', JSON.stringify(cart));
  }, [cart]);

  const calculateTotal = () => {
    if (cart.length === 0) {
      return { subtotal: 0, tax: 0, shipping: 0, total: 0 };
    }

    const subtotal = cart.reduce((acc, item) => acc + (item.price * (item.quantity || 1)), 0);
    const tax = subtotal * 0.05;
    const shipping = 5.00;
    const total = subtotal + tax + shipping;
    return { subtotal, tax, shipping, total };
  };

  const updateCart = (item, action) => {
    setCart(prevCart => {
      const updatedCart = [...prevCart];
      const existingItemIndex = updatedCart.findIndex(cartItem => cartItem.id === item.id);

      if (action === 'add') {
        if (existingItemIndex !== -1) {
          updatedCart[existingItemIndex].quantity = (updatedCart[existingItemIndex].quantity || 1) + 1;
        } else {
          updatedCart.push({ ...item, quantity: 1 });
        }
      } else if (action === 'remove') {
        if (existingItemIndex !== -1) {
          updatedCart[existingItemIndex].quantity = (updatedCart[existingItemIndex].quantity || 1) - 1;
          if (updatedCart[existingItemIndex].quantity <= 0) {
            updatedCart.splice(existingItemIndex, 1);
          }
        }
      }

      console.log("Updated cart:", updatedCart); // Log updated cart
      return updatedCart;
    });
  };

  const { subtotal, tax, shipping, total } = calculateTotal();

  console.log("Cart contents:", cart); // Log cart contents

  return (
    <>
      <header id="site-header">
        <div className="container">
          <h1>Shopping cart</h1>
        </div>
      </header>
      <div className="container">
        <section id="cart">
          {cart.length === 0 ? (
            <p>Your cart is empty</p>
          ) : (
            cart.map((item) => (
              <article className="product" key={item.id}>
                <header>
                  <img src={item.url} alt={item.title} />
                  <div className="remove">
                    <button onClick={() => updateCart(item, 'remove')}>Remove product</button>
                  </div>
                </header>
                <div className="content">
                  <h1>{item.title}</h1>
                  <p>{item.description}</p>
                  <div className="color" style={{ top: 0 }} />
                  <div className="type" style={{ top: 43 }}>{item.category}</div>
                </div>
                <footer className="content">
                  <span className="qt-minus" onClick={() => updateCart(item, 'remove')}>-</span>
                  <span className="qt">{item.quantity}</span>
                  <span className="qt-plus" onClick={() => updateCart(item, 'add')}>+</span>
                  <h2 className="full-price">${item.price.toFixed(2)}</h2>
                  <h2 className="price">${(item.price * item.quantity).toFixed(2)}</h2>
                </footer>
              </article>
            ))
          )}
        </section>
      </div>
      <footer id="site-footer">
        <div className="container clearfix">
          <div className="left">
            <h2 className="subtotal">Subtotal: <span>{subtotal.toFixed(2)}</span>$</h2>
            <h3 className="tax">Taxes (5%): <span>{tax.toFixed(2)}</span>$</h3>
            <h3 className="shipping">Shipping: <span>{shipping.toFixed(2)}</span>$</h3>
          </div>
          <div className="right">
            <h1 className="total">Total: <span>{total.toFixed(2)}</span>$</h1>
            <button className="btn">Checkout</button>
          </div>
        </div>
      </footer>
    </>
  );
}

export default AddToCart;
