import React, { useState } from 'react';
import heroimage from '../assets/img/hero-bg.jpg';
import SignUp from '../Components/Signup'; // Import the SignUp component
import Welcome from '../Components/Welcome'; // Import the Welcome component

function Hero() {
  // Add state to track if the user has signed up
  const [signedUp, setSignedUp] = useState(false);

  // Function to handle successful sign-up
  const handleSignUpSuccess = () => {
    setSignedUp(true);
  };

  // Function to render appropriate content based on sign-up status
  const renderContent = () => {
    if (!signedUp) {
      return <SignUp onSignUpSuccess={handleSignUpSuccess} />;
    } else {
      return <Welcome />;
    }
  };

  return (
    <section id="hero" className="hero section">
      <img src={heroimage} alt="Hero background" />
      <div className="container">
        <div className="row">
          <div className="col-lg-10">
            {renderContent()}
          </div>
        </div>
      </div>
    </section>
  );
}

export default Hero;
