import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { withRouter } from 'react-router-dom';

function Portfolio({ history }) {
  const [portfolio, setPortfolio] = useState([]);
  const [cart, setCart] = useState([]);
  const [authenticated, setAuthenticated] = useState(true); // Assume the user is authenticated initially

  useEffect(() => {
    fetchPortfolio();
    fetchCart();
  }, []);

  const fetchPortfolio = async () => {
    try {
      const response = await axios.get("http://localhost:8002/api/productList");
      const activeProducts = response.data.data.filter(product => !product.is_deleted && product.is_active);
      setPortfolio(activeProducts);
    } catch (error) {
      console.error("Error fetching portfolio:", error);
    }
  };

  const fetchCart = async () => {
    try {
      const response = await axios.get("http://localhost:8002/api/userCart", {
        withCredentials: true // Send cookies for authentication
      });
      setCart(response.data.cart);
    } catch (error) {
      console.error("Error fetching cart:", error);
      setAuthenticated(false); // Set authenticated to false if user is not authenticated
    }
  };

  const addToCart = async (productId) => {
    try {
      await axios.post("http://localhost:8002/api/addToCart", { productId }, {
        withCredentials: true // Send cookies for authentication
      });
      fetchCart(); // Fetch updated cart after adding item
    } catch (error) {
      console.error("Error adding to cart:", error);
    }
  };

  if (!authenticated) {
    // Render login page content or redirect to login page
    return (
      <div>
        <h2>Please log in to view your portfolio and cart</h2>
        <button onClick={() => history.push("/login")}>Go to Login</button>
      </div>
    );
  }

  return (
    <div>
      <h2>Portfolio</h2>
      <div className="portfolio-items">
        {portfolio.map(product => (
          <div key={product.id}>
            <h3>{product.title}</h3>
            <p>{product.description}</p>
            <p>${product.price}</p>
            <button onClick={() => addToCart(product.id)}>Add to Cart</button>
          </div>
        ))}
      </div>
      <h2>Cart</h2>
      <div className="cart-items">
        {cart.map(item => (
          <div key={item.id}>
            <h3>{item.title}</h3>
            <p>{item.description}</p>
            <p>${item.price}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default withRouter(Portfolio);
